package foreach;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int[] tableau = {10, 20, 30, 40, 50}; // Tableau à itérer
	        
	        // Utilisation de la boucle foreach pour itérer sur le tableau
	        for (int element : tableau) {
	            System.out.println("Élément : " + element);
	        }
	}

}
